mapper.insert:
insert into task_run (id, task_id, flow_id, 
  tool_id, busi_type, busi_id, 
  value, create_time, create_by, 
  update_time, update_by, is_deleted
  )
values (#{id,jdbcType=BIGINT}, #{taskId,jdbcType=BIGINT}, #{flowId,jdbcType=BIGINT}, 
  #{toolId,jdbcType=BIGINT}, #{busiType,jdbcType=TINYINT}, #{busiId,jdbcType=BIGINT}, 
  #{value,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createBy,jdbcType=BIGINT}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=BIGINT}, #{isDeleted,jdbcType=TINYINT}
  )

mapper.select:
select

from task_run
where 1=1

  and task_id = #{taskId}


  and flow_id = #{flowId}


  and tool_id = #{toolId}


  and busi_type = #{busiType}


  and busi_id = #{busiId}
