mapper.select:
select
from tool where 1=1
and name LIKE CONCAT('%',#{name},'%')
and type = #{type}
and user_id = #{userId} or user_id = -88
order by create_time desc limit #{start} , #{pageSize}

select

from tool
where is_deleted = 0

  and type = #{type}


  and cate = #{cate}


  and name LIKE CONCAT('%',#{name},'%')


  and user_id = #{userId} or user_id = -88

order by create_time desc

limit #{limit}
